import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState(1);
  const [orderData, setOrderData] = useState({
    shippingAddress: {
      firstName: '',
      lastName: '',
      street: '',
      city: '',
      state: '',
      zipCode: '',
      country: ''
    },
    billingAddress: {
      sameAsShipping: true,
      firstName: '',
      lastName: '',
      street: '',
      city: '',
      state: '',
      zipCode: '',
      country: ''
    },
    payment: {
      method: 'card',
      cardNumber: '',
      expiryDate: '',
      cvv: '',
      nameOnCard: '',
      mpesaPhone: ''
    },
    shipping: {
      method: 'standard'
    }
  });

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }
    if (cartItems.length === 0) {
      navigate('/cart');
      return;
    }
  }, [user, cartItems, navigate]);

  const handleInputChange = (section, field, value) => {
    setOrderData(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value
      }
    }));
  };

  const handleBillingToggle = (sameAsShipping) => {
    setOrderData(prev => ({
      ...prev,
      billingAddress: {
        ...prev.billingAddress,
        sameAsShipping,
        ...(sameAsShipping ? prev.shippingAddress : {})
      }
    }));
  };

  const calculateSubtotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const calculateTax = () => {
    return calculateSubtotal() * 0.08;
  };

  const calculateShipping = () => {
    const shippingRates = {
      standard: calculateSubtotal() > 100 ? 0 : 10,
      express: 25,
      overnight: 50
    };
    return shippingRates[orderData.shipping.method];
  };

  const calculateTotal = () => {
    return calculateSubtotal() + calculateTax() + calculateShipping();
  };

  const validateStep = (stepNumber) => {
    switch (stepNumber) {
      case 1:
        const shipping = orderData.shippingAddress;
        return shipping.firstName && shipping.lastName && shipping.street && 
               shipping.city && shipping.state && shipping.zipCode;
      case 2:
        const payment = orderData.payment;
        if (payment.method === 'card') {
          return payment.cardNumber && payment.expiryDate && payment.cvv && payment.nameOnCard;
        } else if (payment.method === 'mpesa') {
          return payment.mpesaPhone && payment.mpesaPhone.length >= 10;
        }
        return false;
      default:
        return true;
    }
  };

  const handleNextStep = () => {
    if (validateStep(step)) {
      setStep(step + 1);
    }
  };

  const handlePrevStep = () => {
    setStep(step - 1);
  };

  const handleSubmitOrder = async () => {
    setLoading(true);
    try {
      const orderPayload = {
        items: cartItems,
        shippingAddress: orderData.shippingAddress,
        billingAddress: orderData.billingAddress.sameAsShipping ? 
          orderData.shippingAddress : orderData.billingAddress,
        payment: orderData.payment,
        shipping: orderData.shipping,
        totalAmount: calculateTotal()
      };

      const response = await api.post('/api/orders', orderPayload);
      clearCart();
      navigate(`/order-confirmation/${response.data._id}`);
    } catch (error) {
      console.error('Error placing order:', error);
      alert('Error placing order. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (cartItems.length === 0) {
    return null;
  }

  return (
    <div className="checkout-page">
      <div className="container">
        <div className="checkout-header">
          <h1>Checkout</h1>
          <div className="checkout-steps">
            <div className={`step ${step >= 1 ? 'active' : ''}`}>
              <span className="step-number">1</span>
              <span className="step-label">Shipping</span>
            </div>
            <div className={`step ${step >= 2 ? 'active' : ''}`}>
              <span className="step-number">2</span>
              <span className="step-label">Payment</span>
            </div>
            <div className={`step ${step >= 3 ? 'active' : ''}`}>
              <span className="step-number">3</span>
              <span className="step-label">Review</span>
            </div>
          </div>
        </div>

        <div className="checkout-layout">
          <div className="checkout-form">
            {step === 1 && (
              <div className="checkout-step">
                <h2>Shipping Information</h2>
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="shippingFirstName">First Name</label>
                    <input
                      type="text"
                      id="shippingFirstName"
                      value={orderData.shippingAddress.firstName}
                      onChange={(e) => handleInputChange('shippingAddress', 'firstName', e.target.value)}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="shippingLastName">Last Name</label>
                    <input
                      type="text"
                      id="shippingLastName"
                      value={orderData.shippingAddress.lastName}
                      onChange={(e) => handleInputChange('shippingAddress', 'lastName', e.target.value)}
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="shippingStreet">Street Address</label>
                  <input
                    type="text"
                    id="shippingStreet"
                    value={orderData.shippingAddress.street}
                    onChange={(e) => handleInputChange('shippingAddress', 'street', e.target.value)}
                    required
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="shippingCity">City</label>
                    <input
                      type="text"
                      id="shippingCity"
                      value={orderData.shippingAddress.city}
                      onChange={(e) => handleInputChange('shippingAddress', 'city', e.target.value)}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="shippingState">State</label>
                    <input
                      type="text"
                      id="shippingState"
                      value={orderData.shippingAddress.state}
                      onChange={(e) => handleInputChange('shippingAddress', 'state', e.target.value)}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="shippingZipCode">ZIP Code</label>
                    <input
                      type="text"
                      id="shippingZipCode"
                      value={orderData.shippingAddress.zipCode}
                      onChange={(e) => handleInputChange('shippingAddress', 'zipCode', e.target.value)}
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="shippingCountry">Country</label>
                  <input
                    type="text"
                    id="shippingCountry"
                    value={orderData.shippingAddress.country}
                    onChange={(e) => handleInputChange('shippingAddress', 'country', e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="shippingMethod">Shipping Method</label>
                  <select
                    id="shippingMethod"
                    value={orderData.shipping.method}
                    onChange={(e) => handleInputChange('shipping', 'method', e.target.value)}
                  >
                    <option value="standard">Standard (3-5 days) - {calculateSubtotal() > 100 ? 'FREE' : '$10'}</option>
                    <option value="express">Express (1-2 days) - $25</option>
                    <option value="overnight">Overnight - $50</option>
                  </select>
                </div>

                <div className="step-actions">
                  <button 
                    className="next-btn"
                    onClick={handleNextStep}
                    disabled={!validateStep(1)}
                  >
                    Continue to Payment
                  </button>
                </div>
              </div>
            )}

            {step === 2 && (
              <div className="checkout-step">
                <h2>Payment Information</h2>
                
                <div className="form-group">
                  <label htmlFor="billingSameAsShipping">
                    <input
                      type="checkbox"
                      id="billingSameAsShipping"
                      checked={orderData.billingAddress.sameAsShipping}
                      onChange={(e) => handleBillingToggle(e.target.checked)}
                    />
                    Billing address same as shipping address
                  </label>
                </div>

                <div className="form-group">
                  <label>Payment Method</label>
                  <div className="payment-methods">
                    <div className="payment-method-option">
                      <input
                        type="radio"
                        id="card-payment"
                        name="paymentMethod"
                        value="card"
                        checked={orderData.payment.method === 'card'}
                        onChange={(e) => handleInputChange('payment', 'method', e.target.value)}
                      />
                      <label htmlFor="card-payment" className="payment-method-label">
                        <span className="payment-icon">💳</span>
                        <span>Credit/Debit Card</span>
                      </label>
                    </div>
                    <div className="payment-method-option">
                      <input
                        type="radio"
                        id="mpesa-payment"
                        name="paymentMethod"
                        value="mpesa"
                        checked={orderData.payment.method === 'mpesa'}
                        onChange={(e) => handleInputChange('payment', 'method', e.target.value)}
                      />
                      <label htmlFor="mpesa-payment" className="payment-method-label">
                        <span className="payment-icon">📱</span>
                        <span>MPesa</span>
                      </label>
                    </div>
                  </div>
                </div>

                {!orderData.billingAddress.sameAsShipping && (
                  <div className="billing-address">
                    <h3>Billing Address</h3>
                    <div className="form-row">
                      <div className="form-group">
                        <label htmlFor="billingFirstName">First Name</label>
                        <input
                          type="text"
                          id="billingFirstName"
                          value={orderData.billingAddress.firstName}
                          onChange={(e) => handleInputChange('billingAddress', 'firstName', e.target.value)}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="billingLastName">Last Name</label>
                        <input
                          type="text"
                          id="billingLastName"
                          value={orderData.billingAddress.lastName}
                          onChange={(e) => handleInputChange('billingAddress', 'lastName', e.target.value)}
                        />
                      </div>
                    </div>
                    {/* Add more billing address fields as needed */}
                  </div>
                )}

                <div className="form-group">
                  <label htmlFor="nameOnCard">Name on Card</label>
                  <input
                    type="text"
                    id="nameOnCard"
                    value={orderData.payment.nameOnCard}
                    onChange={(e) => handleInputChange('payment', 'nameOnCard', e.target.value)}
                    required
                  />
                </div>

                {orderData.payment.method === 'card' && (
                  <>
                    <div className="form-group">
                      <label htmlFor="nameOnCard">Name on Card</label>
                      <input
                        type="text"
                        id="nameOnCard"
                        value={orderData.payment.nameOnCard}
                        onChange={(e) => handleInputChange('payment', 'nameOnCard', e.target.value)}
                        placeholder="John Doe"
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="cardNumber">Card Number</label>
                      <input
                        type="text"
                        id="cardNumber"
                        value={orderData.payment.cardNumber}
                        onChange={(e) => handleInputChange('payment', 'cardNumber', e.target.value)}
                        placeholder="1234 5678 9012 3456"
                        required
                      />
                    </div>

                    <div className="form-row">
                      <div className="form-group">
                        <label htmlFor="expiryDate">Expiry Date</label>
                        <input
                          type="text"
                          id="expiryDate"
                          value={orderData.payment.expiryDate}
                          onChange={(e) => handleInputChange('payment', 'expiryDate', e.target.value)}
                          placeholder="MM/YY"
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="cvv">CVV</label>
                        <input
                          type="text"
                          id="cvv"
                          value={orderData.payment.cvv}
                          onChange={(e) => handleInputChange('payment', 'cvv', e.target.value)}
                          placeholder="123"
                          required
                        />
                      </div>
                    </div>
                  </>
                )}

                {orderData.payment.method === 'mpesa' && (
                  <div className="mpesa-payment">
                    <div className="mpesa-info">
                      <h3>📱 MPesa Payment</h3>
                      <p>You will receive an MPesa prompt on your phone to complete the payment.</p>
                    </div>
                    
                    <div className="form-group">
                      <label htmlFor="mpesaPhone">Phone Number</label>
                      <input
                        type="tel"
                        id="mpesaPhone"
                        value={orderData.payment.mpesaPhone}
                        onChange={(e) => handleInputChange('payment', 'mpesaPhone', e.target.value)}
                        placeholder="+254 7XX XXX XXX"
                        required
                      />
                      <small>Enter your MPesa registered phone number</small>
                    </div>

                    <div className="mpesa-instructions">
                      <h4>How it works:</h4>
                      <ol>
                        <li>Enter your MPesa registered phone number</li>
                        <li>Click "Review Order" to proceed</li>
                        <li>You'll receive an MPesa prompt on your phone</li>
                        <li>Enter your MPesa PIN to complete payment</li>
                      </ol>
                    </div>
                  </div>
                )}

                <div className="step-actions">
                  <button 
                    className="prev-btn"
                    onClick={handlePrevStep}
                  >
                    Back to Shipping
                  </button>
                  <button 
                    className="next-btn"
                    onClick={handleNextStep}
                    disabled={!validateStep(2)}
                  >
                    Review Order
                  </button>
                </div>
              </div>
            )}

            {step === 3 && (
              <div className="checkout-step">
                <h2>Review Your Order</h2>
                
                <div className="order-review">
                  <div className="review-section">
                    <h3>Shipping Address</h3>
                    <p>
                      {orderData.shippingAddress.firstName} {orderData.shippingAddress.lastName}<br />
                      {orderData.shippingAddress.street}<br />
                      {orderData.shippingAddress.city}, {orderData.shippingAddress.state} {orderData.shippingAddress.zipCode}<br />
                      {orderData.shippingAddress.country}
                    </p>
                  </div>

                  <div className="review-section">
                    <h3>Payment Method</h3>
                    {orderData.payment.method === 'card' ? (
                      <p>
                        **** **** **** {orderData.payment.cardNumber.slice(-4)}<br />
                        {orderData.payment.nameOnCard}
                      </p>
                    ) : (
                      <p>
                        📱 MPesa<br />
                        {orderData.payment.mpesaPhone}
                      </p>
                    )}
                  </div>

                  <div className="review-section">
                    <h3>Order Items</h3>
                    <div className="order-items">
                      {cartItems.map(item => (
                        <div key={item._id} className="order-item">
                          <img src={item.image} alt={item.name} />
                          <div>
                            <h4>{item.name}</h4>
                            <p>Quantity: {item.quantity}</p>
                            <p>${(item.price * item.quantity).toFixed(2)}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                <div className="step-actions">
                  <button 
                    className="prev-btn"
                    onClick={handlePrevStep}
                  >
                    Back to Payment
                  </button>
                  <button 
                    className="place-order-btn"
                    onClick={handleSubmitOrder}
                    disabled={loading}
                  >
                    {loading ? 'Placing Order...' : 'Place Order'}
                  </button>
                </div>
              </div>
            )}
          </div>

          <div className="checkout-summary">
            <div className="summary-card">
              <h3>Order Summary</h3>
              
              <div className="summary-row">
                <span>Subtotal:</span>
                <span>${calculateSubtotal().toFixed(2)}</span>
              </div>
              
              <div className="summary-row">
                <span>Tax:</span>
                <span>${calculateTax().toFixed(2)}</span>
              </div>
              
              <div className="summary-row">
                <span>Shipping:</span>
                <span>
                  {calculateShipping() === 0 ? 'FREE' : `$${calculateShipping().toFixed(2)}`}
                </span>
              </div>
              
              <div className="summary-row total">
                <span>Total:</span>
                <span>${calculateTotal().toFixed(2)}</span>
              </div>
            </div>

            <div className="security-info">
              <h4>🔒 Secure Checkout</h4>
              <p>Your payment information is encrypted and secure.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
