import { formatKES } from '../utils/currency';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import SearchBar from '../components/SearchBar';
import { productsAPI } from '../services/api';
import './Products.css';

// Hard-coded price bounds
// Use underscores for better readability in large numbers
const HARD_MIN_PRICE = 100;
const HARD_MAX_PRICE = 1000000;

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [priceRange, setPriceRange] = useState([HARD_MIN_PRICE, HARD_MAX_PRICE]);
  const [maxPrice] = useState(HARD_MAX_PRICE); // No longer dynamic, always max
  const [sortBy, setSortBy] = useState('name');

  const categories = [
    { value: 'all', label: 'All Products' },
    { value: 'smartphones', label: 'Smartphones' },
    { value: 'accessories', label: 'Accessories' },
    // Add more categories if needed
  ];

  // Fetch products and set hard-coded initial price range on load
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await productsAPI.getAll();
        const productsData = response?.data?.products || [];
        setProducts(productsData);
        // Hard-coded bounds (do not calculate from products)
        setPriceRange([HARD_MIN_PRICE, HARD_MAX_PRICE]);
      } catch (error) {
        console.error('Error fetching products:', error);
        setProducts([]);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // Filter products based on search, category, and hard-coded price range
  const filterProducts = () => {
    let filtered = [...products];

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by price range (using hard bounds)
    filtered = filtered.filter(product =>
      product.price >= priceRange[0] && product.price <= priceRange[1]
    );

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'name':
          return a.name.localeCompare(b.name);
        case 'rating':
          return (b.rating || 0) - (a.rating || 0);
        default:
          return 0;
      }
    });

    setFilteredProducts(filtered);
  };

  // Input handlers with hard-coded bounds validation
  const handleSearch = (term) => setSearchTerm(term);
  const handleCategoryChange = (category) => setSelectedCategory(category);

  const handleMinPriceChange = (value) => {
    let newMin = Math.max(HARD_MIN_PRICE, Math.min(Number(value), priceRange[1]));
    setPriceRange([newMin, priceRange[1]]);
  };

  const handleMaxPriceChange = (value) => {
    let newMax = Math.min(HARD_MAX_PRICE, Math.max(Number(value), priceRange[0]));
    setPriceRange([priceRange[0], newMax]);
  };

  const resetPriceRange = () => setPriceRange([HARD_MIN_PRICE, HARD_MAX_PRICE]);

  const handleSortChange = (sort) => setSortBy(sort);

  // Update CSS custom properties for slider range indicator
  useEffect(() => {
    const sliderContainer = document.querySelector('.price-slider-container');
    if (sliderContainer) {
      const minPercent = ((priceRange[0] - HARD_MIN_PRICE) / (HARD_MAX_PRICE - HARD_MIN_PRICE)) * 100;
      const maxPercent = ((priceRange[1] - HARD_MIN_PRICE) / (HARD_MAX_PRICE - HARD_MIN_PRICE)) * 100;
      sliderContainer.style.setProperty('--min-percent', minPercent);
      sliderContainer.style.setProperty('--max-percent', maxPercent);
    }
  }, [priceRange]);

  // Filter whenever dependencies change
  useEffect(() => {
    filterProducts();
    // eslint-disable-next-line
  }, [products, searchTerm, selectedCategory, priceRange, sortBy]);

  if (loading) {
    return (
      <div className="products-loading">
        <div className="loading-spinner"></div>
        <p>Loading products...</p>
      </div>
    );
  }

  return (
    <div className="products-page">
      <div className="container">
        <div className="page-header">
          <h1>Our Products</h1>
          <p>Discover the latest mobile phones and accessories</p>
        </div>

        <div className="products-layout">
          <div className="filters-sidebar">
            <div className="filter-section">
              <h3>Search</h3>
              <SearchBar onSearch={handleSearch} placeholder="Search products..." />
            </div>
            <div className="filter-section">
              <h3>Category</h3>
              <div className="category-filters">
                {categories.map(category => (
                  <button
                    key={category.value}
                    className={`category-btn ${selectedCategory === category.value ? 'active' : ''}`}
                    onClick={() => handleCategoryChange(category.value)}
                  >
                    {category.label}
                  </button>
                ))}
              </div>
            </div>
            {/* Price Range Filter with Hard-coded Bounds */}
            <div className="filter-section">
              <h3>Price Range</h3>
              <div className="price-range">
                <div className="price-inputs">
                  <div className="price-input-group">
                    <label>Min Price</label>
                    <input
                      type="number"
                      min={HARD_MIN_PRICE}
                      max={HARD_MAX_PRICE}
                      value={priceRange[0]}
                      onChange={e => handleMinPriceChange(e.target.value)}
                      className="price-input"
                    />
                  </div>
                  <div className="price-input-group">
                    <label>Max Price</label>
                    <input
                      type="number"
                      min={HARD_MIN_PRICE}
                      max={HARD_MAX_PRICE}
                      value={priceRange[1]}
                      onChange={e => handleMaxPriceChange(e.target.value)}
                      className="price-input"
                    />
                  </div>
                </div>
                <div className="price-slider-container">
                  <input
                    type="range"
                    min={HARD_MIN_PRICE}
                    max={HARD_MAX_PRICE}
                    value={priceRange[0]}
                    onChange={e => handleMinPriceChange(e.target.value)}
                    className="price-slider price-slider-min"
                  />
                  <input
                    type="range"
                    min={HARD_MIN_PRICE}
                    max={HARD_MAX_PRICE}
                    value={priceRange[1]}
                    onChange={e => handleMaxPriceChange(e.target.value)}
                    className="price-slider price-slider-max"
                  />
                </div>
                <div className="price-labels">
                  <span>{formatKES(HARD_MIN_PRICE)}</span>
                  <span className="price-range-display">
                    {formatKES(priceRange[0])} - {formatKES(priceRange[1])}
                  </span>
                  <span>{formatKES(HARD_MAX_PRICE)}</span>
                </div>
                <button
                  type="button"
                  onClick={resetPriceRange}
                  className="price-reset-btn"
                >
                  Reset Range
                </button>
              </div>
            </div>
            <div className="filter-section">
              <h3>Sort By</h3>
              <select
                value={sortBy}
                onChange={e => handleSortChange(e.target.value)}
                className="sort-select"
              >
                <option value="name">Name (A-Z)</option>
                <option value="price-low">Price (Low to High)</option>
                <option value="price-high">Price (High to Low)</option>
                <option value="rating">Rating</option>
              </select>
            </div>
          </div>
          <div className="products-content">
            <div className="products-header">
              <p className="results-count">
                Showing {filteredProducts.length} of {products.length} products
              </p>
            </div>
            {filteredProducts.length === 0 ? (
              <div className="no-products">
                <h3>No products found</h3>
                <p>Try adjusting your search criteria</p>
              </div>
            ) : (
              <div className="products-grid">
                {filteredProducts.map(product => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
